mapper.select:
SELECT * FROM t_open_door_record


		style=#{style}


		and keyname IN (SELECT keyname FROM t_unlockingkey WHERE
		p_m_id=#{pmid})


ORDER BY createtime desc
LIMIT #{startCount},#{pageSize}

SELECT * FROM t_open_door_record
style=#{style}
and keyname IN (SELECT keyname FROM t_unlockingkey WHERE p_m_id=#{pmid})
and keyname IN (SELECT keyname FROM t_unlockingkey WHERE p_m_id IN (SELECT id FROM t_property_management_department WHERE pid=#{pid}) GROUP BY keyname)
ORDER BY createtime desc LIMIT #{startCount},#{pageSize}

SELECT COUNT(*) FROM t_open_door_record


		style=#{style}


		and keyname IN (SELECT keyname FROM t_unlockingkey WHERE p_m_id=#{pmid})
